import React, {useEffect, useState} from 'react' ;
import './App.css';
import MovieBox from './MovieBox';

const API_URL = "https://api.themoviedb.org/3/movie/popular?api_key=dc1c29ce98e6e5cf8dedec5d6a33813e"
function App() { 

  const  [movies, setMovies] = useState([]) ;

  useEffect(() => {
      fetch(API_URL) 
      .then((res)=>res.json()) 
      .then(data=> {
        console.log(data);
        setMovies(data.results);
      })
  }, [])

  return (
    <div>
      {movies.map((moviesReq => <MovieBox/>))} 
      <MovieBox key={moviesReq.id} {...moviesReq} />
    </div>
  );
}

export default App;
